import { Component } from '@angular/core';
import { Produtos } from '../produtos/produtos.model';
import { ProdutosService } from '../service/produtos.service';
import { CarrinhoService } from '../service/carrinho.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-lista-produtos',
  templateUrl: './lista-produtos.component.html',
  styleUrls: ['./lista-produtos.component.css']
})
export class ListaProdutosComponent {
  lista :  Produtos[] = [];
  mensagem : String = "";
  nomeUsuario: String = "";
  quantidadeDesejada: number = 1;

  constructor(
    private produtosService : ProdutosService,
    private carrinhoService: CarrinhoService,
    private router: Router){}

  ngOnInit(){
    this.listar();
    let nome = localStorage.getItem("nomeCliente");
    if(nome!=null) this.nomeUsuario = "Olá, "+ nome ;
  }
  public listar(){
    try{
      this.produtosService.listar().subscribe(
        (retorno: Produtos[]) => {            
          console.log(retorno);
          this.lista = retorno;
          this.mensagem = "";
        },
          error => {
          console.error(error);  // Adicione este log para verificar se há erros na chamada.
          this.mensagem = "Erro ao obter a lista de produtos.";
        }
        );
    } catch {
      this.mensagem = "não foi encontrado nenhum registro !";
    }
  }

  fazerLogoff(){
    localStorage.clear();  
    this.nomeUsuario="";
    window.location.href="./login";
  }

  adicionarAoCarrinho(produto: Produtos, quantidade_estoque: number) {
    this.carrinhoService.adicionarAoCarrinho(produto, quantidade_estoque);
    this.router.navigate(['/carrinho']);
    // todo msg de adicionado com sucesso 
  }

}
