import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class CarrinhoService {
  //   private carrinhoItems: any[] = [];

  //   adicionarAoCarrinho(item: any, quantidade_estoque: number = 1) {
  //       // Verificar se o item já está no carrinho
  //       const itemExistente = this.carrinhoItems.find((i) => i.id === item.id);
    
  //       if (itemExistente) {
  //         itemExistente.quantidade += quantidade_estoque;
  //       } else {
  //           this.carrinhoItems.push({ ...item, quantidade_estoque });
  //       }
  //   }    

  // removerDoCarrinho(item: any) {
    
  //   const index = this.carrinhoItems.indexOf(item);
  //   if (index !== -1) {
  //     this.carrinhoItems.splice(index, 1);
  //   }
  // }

  // obterCarrinhoItems() {
    
  //   return this.carrinhoItems;
  // }

  // limparCarrinho() {
    
  //   this.carrinhoItems = [];
  // }
  private chaveCarrinho = 'carrinho';

  private carrinhoSubject: BehaviorSubject<{ [key: string]: any[] }> = new BehaviorSubject<{ [key: string]: any[] }>({});

  constructor() {
    const carrinhoString = localStorage.getItem(this.chaveCarrinho);
    const carrinho = carrinhoString ? JSON.parse(carrinhoString) : {};
    this.carrinhoSubject.next(carrinho);
  }

  adicionarAoCarrinho(produto: any): void {
    const carrinhoAtual = { ...this.carrinhoSubject.value };

    // Gere uma chave única para o pedido (pode ser um ID aleatório)
    const idPedido = this.gerarIdPedido();

    if (!carrinhoAtual[idPedido]) {
      carrinhoAtual[idPedido] = [];
    }

    // Adiciona o produto ao pedido
    carrinhoAtual[idPedido].push(produto);
    this.atualizarCarrinho(carrinhoAtual);
  }

  public obterCarrinho(): Observable<{ [key: string]: any[] }> {
    return this.carrinhoSubject.asObservable();
  }

  private atualizarCarrinho(carrinho: { [key: string]: any[] }): void {
    localStorage.setItem(this.chaveCarrinho, JSON.stringify(carrinho));
    this.carrinhoSubject.next(carrinho);
  }

  limparCarrinho(): void {
    this.atualizarCarrinho({});
  }

  // Função auxiliar para gerar um ID de pedido (pode ser ajustada conforme necessário)
  private gerarIdPedido(): string {
    return 'pedido_' + Date.now().toString();
  }
}
