import { Component } from '@angular/core';

@Component({
  selector: 'app-esqueci-senha',
  templateUrl: './esqueci-senha.component.html',
  styleUrls: ['./esqueci-senha.component.css']
})
export class EsqueciSenhaComponent {

  email: string;

  constructor(private service: ClienteService) {
    // Agora você pode usar this.service no componente para interagir com o serviço
  }

  enviarEmailRecuperacao(){
    try{
        this.service.enviarEmailRecuperacao(this.email).subscribe(
          (retorno: any) => {
            // Lógica de tratamento da resposta do serviço de recuperação de senha
            // Pode incluir exibir mensagens na interface do usuário ou redirecionar para outra página
            console.log(retorno);
          },
          (erro: any) => {
            console.error('Erro ao enviar email de recuperação:', erro);
            this.mensagem = 'Ocorreu um erro ao enviar o email de recuperação. Tente novamente mais tarde.';
          }
        );
      } catch (error) {
        console.error('Erro:', error);
        this.mensagem = 'Ocorreu um erro. Tente novamente mais tarde.';
      }
    }
}
