import { Component } from '@angular/core';
import { Produtos } from './produtos.model';
import { ProdutosService } from '../service/produtos.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css']
})
export class ProdutosComponent {
  mensagem : string = "";
  obj: Produtos = new Produtos();
  lista: Produtos[] = [];

  constructor(private produtosService: ProdutosService, 
              private rotas: ActivatedRoute) {}

  ngOnInit(){
    let codigo = this.rotas.snapshot.paramMap.get("codigo");
    if(codigo!=null){
      this.obj.codigo = Number(codigo);
      this.pesquisar();
    }     
  }              

  public mostrar(){
    this.mensagem = "Registro gravado com sucesso!";
    let produtosAux : Produtos = new Produtos(this.obj);    
    this.lista.push(produtosAux);
    window.localStorage.setItem('listaProdutos', JSON.stringify(this.lista));
  }

  public gravar(){
    try{
      if(this.obj.codigo==0){
        this.produtosService.gravar(this.obj);
        this.mensagem = "Produto gravado com sucesso!";
      }  else {
        this.produtosService.alterar(this.obj);
        this.mensagem = "Produtp alterado com sucesso!";
      }      
    }
    catch{
      this.mensagem = "Ocorreu um erro, verifique!";
    }
  }

  public pesquisar(){
    this.mensagem = "";
    try{      
      this.produtosService.carregar(this.obj.codigo).subscribe(
        (retorno: Produtos) => {            
          this.obj.nome_produto = retorno.nome_produto;
          this.obj.descricao = retorno.descricao;
          this.obj.preco = retorno.preco;
          this.obj.categoria = retorno.categoria;
          if(retorno.nome_produto==null)  this.mensagem = "Registro não encontrado!";          
        });
    }
    catch{
      this.mensagem = "Ocorreu um erro, verifique!";
    }
  }

  public remover(){
    try{
      this.produtosService.remover(this.obj.codigo);
      this.mensagem = "Produto removido com sucesso!";
    } 
    catch{
      this.mensagem = "Ocorreu um erro durante o processo!";
    }     
  }
}






