import { Component } from '@angular/core';
import { ClienteService } from '../service/cliente.service';
import { Cliente } from '../cliente/cliente.model';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  mensagem: String = "";
  obj : Cliente = new Cliente();

  constructor(private service:ClienteService ){ }

  fazerLogin(){
    try{
        this.service.login(this.obj).subscribe(
          (retorno: Cliente) => {      
              if(retorno.nome==null){
                this.mensagem = "email ou senha invalidos!!!";  
              }  else {
                localStorage.setItem("nomeCliente", retorno.nome);  
                localStorage.setItem("cliente", JSON.stringify(retorno));
                window.location.href = "./lista";
              }   
          });
    }
    catch{
      this.mensagem = "Ocorreu um erro volte mais tarde!";
    }
  }
}
<h2>Login</h2>

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous">
    <title>Entrar</title>
</head>


<div class="alert alert-danger"
*ngIf="mensagem.length>0">{{mensagem}}</div>

<div class="form-label">email:</div>
<input type="email" [(ngModel)]="obj.email" 
class="form-control" ><br>
<div class="form-label">senha:</div>
<input type="password" [(ngModel)]="obj.senha"
class="form-control" ><br>
<button class="btn btn-success"
(click)="fazerLogin();">Entrar</button>
