import { Component } from '@angular/core';
import { Cliente } from './cliente.model';

@Component({
  selector: 'app-cliente',
  templateUrl: './cliente.component.html',
  styleUrls: ['./cliente.component.css']
})
export class ClienteComponent {
  mensagem : string = "";
  obj: Cliente = new Cliente();
  lista: Cliente[] = [];

  public mostrar(){
    this.mensagem = "Registro gravado com sucesso!";
    let clienteAux : Cliente = new Cliente();
    clienteAux.codigo = this.obj.codigo;
    clienteAux.email = this.obj.email;
    clienteAux.nome  = this.obj.nome;
    clienteAux.senha = this.obj.senha;
    clienteAux.telefone = this.obj.telefone;
    this.lista.push(clienteAux);
    console.log(this.lista);
  }
}
<h2>Cadastro de Cliente</h2>
<div class="alert alert-info"
 *ngIf="mensagem.length>0">{{mensagem}}</div>

Codigo:<input type="number" [(ngModel)]="obj.codigo" 
class="form-control"><br>
Nome:<input type="text" [(ngModel)]="obj.nome" 
class="form-control"><br>
Email:<input type="text" [(ngModel)]="obj.email"
class="form-control"> <br>
Telefone:<input type="text" [(ngModel)]="obj.telefone"
class="form-control"><br>
Senha:<input type="password" [(ngModel)]="obj.senha"
class="form-control"><br>

<button (click)="mostrar()" class="btn btn-success">
    Gravar</button>

<div class="alert alert-info"></div>
<table class="table table-striped" *ngIf="lista.length>0">
<tr><td>codigo</td><td>nome</td><td>email</td><td>telefone</td></tr> 
<tr *ngFor="let item of lista">
    <td>{{item.codigo}}</td>   
    <td>{{item.nome}}</td>   
    <td>{{item.email}}</td>   
    <td>{{item.telefone}}</td>   
</tr>
</table>
